import './Main.css'
import FloatingLabel from 'react-bootstrap/esm/FloatingLabel';
import 
{Form,
  Button,
  Card}
   from 'react-bootstrap/Form'



import React, { useEffect, useState } from 'react';


function Main (){
  const [title,setTitle] =useState()
  const [content,setContent]=useState()
  const [postList,setPostList]=useState({title:'', content:''})

  const post = ()=>{
    setPostList({title: title, content:content})
  }
  useEffect(() => {
		postList();
	}, [title, content]);

  const handleChange = (event) => {
		setTitle(event.target.value);
	};
	async function handleClick(e) {
		const response = await fetch(
			'http://localhost:8000/ideas/',
			{
				method: 'POST',
				headers: {
					'Content-Type': 'application/json',
				},
				body: JSON.stringify(postList),
			}
		);
	}


return(

  <div>
      <>
    <FloatingLabel controlId="floatingTextarea" label="title" className="mb-3">
    <Form.Control as="textarea" placeholder="Leave a comment here" onChange={handleChange}/>
  </FloatingLabel>
  <FloatingLabel controlId="floatingTextarea2" label="Contents">
    <Form.Control
      as="textarea"
      placeholder="Leave a comment here"
      style={{ height: '100px' }}
      onChange={handleChange}
    />
  </FloatingLabel>
</>
<Button type="submit" onClick={handleClick}>submit</Button>
    <ul>
      <Card style={{ width: '18rem' }}>
    <Card.Body>
    <Card.Title>Card Title</Card.Title>
    <Card.Subtitle className="mb-2 text-muted">Card Subtitle</Card.Subtitle>
    <Card.Text>
      Some quick example text to build on the card title and make up the bulk of
      the card's content.
    </Card.Text>
    <Card.Link href="#">Card Link</Card.Link>
    <Card.Link href="#">Another Link</Card.Link>
  </Card.Body>
</Card>
    </ul>
    </div>

)
}

export default Main
